// C:\Users\ASUS\meditrack-ai\frontend\src\components\Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, signInWithEmailAndPassword } from '../firebase';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      await signInWithEmailAndPassword(auth, email, password);
      console.log('User logged in successfully!');
      navigate('/dashboard');
    } catch (err) {
      if (err.code === 'auth/user-not-found' || err.code === 'auth/wrong-password') {
        setError('Invalid email or password. Please try again.');
      } else if (err.code === 'auth/invalid-email') {
        setError('The email address is not valid.');
      } else if (err.code === 'auth/too-many-requests') {
        setError('Too many failed login attempts. Please try again later.');
      } else {
        setError(err.message);
      }
      console.error('Error logging in:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      maxWidth: '450px', /* Wider form */
      margin: '50px auto',
      padding: '40px', /* More padding */
      border: '1px solid #e0e0e0', /* Lighter border */
      borderRadius: '12px', /* More rounded corners */
      boxShadow: '0 5px 15px rgba(0,0,0,0.08)', /* Softer, larger shadow */
      backgroundColor: '#fff'
    }}>
      <h2 style={{ textAlign: 'center', color: '#333', marginBottom: '30px', fontSize: '2em', fontWeight: 700 }}>Login to Your Account</h2>
      <form onSubmit={handleLogin} style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
        <input
          type="email"
          placeholder="Email Address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
        />
        <button type="submit" disabled={loading} style={{
          padding: '12px 15px',
          background: '#007bff', /* Blue button */
          color: 'white',
          fontSize: '1.1em'
        }}>
          {loading ? 'Logging In...' : 'Login'}
        </button>
      </form>
      {error && <p style={{ color: '#dc3545', marginTop: '20px', textAlign: 'center', fontWeight: 500 }}>{error}</p>}
      <p style={{ marginTop: '25px', textAlign: 'center', fontSize: '0.95em' }}>
        Don't have an account? {' '}
        <button onClick={() => navigate('/signup')} style={{
          background: 'none',
          border: 'none',
          color: '#6f42c1',
          cursor: 'pointer',
          textDecoration: 'underline',
          padding: '0',
          fontSize: '0.95em',
          fontWeight: 500
        }}>
          Sign Up
        </button>
      </p>
    </div>
  );
}

export default Login;
