// C:\Users\ASUS\meditrack-ai\frontend\src\components\Dashboard.js
import React from 'react';
import { useAuth } from '../contexts/AuthContext'; // To get current user and logout function
import { useNavigate } from 'react-router-dom'; // For navigation after logout
import ReportUploadForm from './ReportUploadForm'; // Import the report upload form component

function Dashboard() {
  const { currentUser, userProfile, logout, loading } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout(); // Call the logout function from AuthContext
      navigate('/login'); // Redirect to login page after logout
    } catch (error) {
      console.error('Error logging out:', error);
      alert('Failed to log out. Please try again.');
    }
  };

  if (loading) {
    return <div style={{ textAlign: 'center', marginTop: '50px', fontSize: '1.2em', color: '#666' }}>Loading dashboard...</div>;
  }

  // Fallback if currentUser is null (PrivateRoute should prevent this normally)
  if (!currentUser) {
    navigate('/login');
    return null;
  }

  return (
    <div className="dashboard-container" style={{ fontFamily: 'Arial, sans-serif', maxWidth: '1000px', margin: '20px auto', border: '1px solid #ddd', borderRadius: '8px', boxShadow: '0 4px 8px rgba(0,0,0,0.05)' }}>
      <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '15px 25px', background: '#f0f0f0', borderBottom: '1px solid #ddd', borderRadius: '8px 8px 0 0' }}>
        <h2 style={{ margin: 0, color: '#333' }}>
          Welcome, {userProfile ? userProfile.name : currentUser.email}!
        </h2>
        <button
          onClick={handleLogout}
          style={{
            padding: '10px 20px',
            background: '#dc3545', // Red color for logout
            color: 'white',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
            fontSize: '1em'
          }}
        >
          Logout
        </button>
      </nav>

      <div style={{ padding: '25px' }}>
        <h3 style={{ color: '#007bff', marginBottom: '20px' }}>Your Meditrack AI Dashboard</h3>
        <p style={{ fontSize: '1.1em', color: '#555' }}>
          Here you can upload your medical reports for AI analysis and view your past records.
        </p>

        {/* Integrate the ReportUploadForm component here */}
        <ReportUploadForm />

        {/* Section for displaying recent reports or history */}
        <div style={{ marginTop: '40px', borderTop: '1px solid #eee', paddingTop: '30px' }}>
          <h4 style={{ color: '#333' }}>Recent Reports & History</h4>
          <p style={{ color: '#777' }}>No recent reports to display yet. Upload one above to get started!</p>
          {/* Future: Add logic here to fetch and display user's past reports from Firestore */}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
