// C:\Users\ASUS\meditrack-ai\frontend\src\components\Home.js
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';

function Home() {
  const { currentUser } = useAuth();

  return (
    <div style={{
      textAlign: 'center',
      marginTop: '50px',
      padding: '40px', /* More padding */
      border: '1px solid #eee',
      borderRadius: '12px', /* More rounded corners */
      maxWidth: '800px',
      margin: '50px auto',
      boxShadow: '0 5px 15px rgba(0,0,0,0.08)', /* Softer, larger shadow */
      backgroundColor: '#fff'
    }}>
      <h2 style={{ color: '#007bff', fontSize: '2em', marginBottom: '20px', fontWeight: 600 }}>Welcome to Meditrack AI!</h2>
      <p style={{ fontSize: '1.15em', color: '#555', lineHeight: '1.8', marginBottom: '30px' }}>
        Your intelligent assistant for analyzing medical reports, providing clear insights and actionable feedback.
      </p>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '20px' }}>
        {currentUser ? (
          <Link to="/dashboard" style={{
            textDecoration: 'none',
            padding: '12px 28px',
            background: '#28a745', /* Green for Dashboard */
            color: 'white',
            borderRadius: '8px',
            fontWeight: 600,
            transition: 'background-color 0.2s ease'
          }}>Go to Dashboard</Link>
        ) : (
          <>
            <Link to="/signup" style={{
              textDecoration: 'none',
              padding: '12px 28px',
              background: '#6f42c1', /* Purple for Signup */
              color: 'white',
              borderRadius: '8px',
              fontWeight: 600,
              transition: 'background-color 0.2s ease'
            }}>Sign Up</Link>
            <Link to="/login" style={{
              textDecoration: 'none',
              padding: '12px 28px',
              background: '#007bff', /* Blue for Login */
              color: 'white',
              borderRadius: '8px',
              fontWeight: 600,
              transition: 'background-color 0.2s ease'
            }}>Login</Link>
          </>
        )}
      </div>
      <p style={{ marginTop: '50px', fontSize: '0.9em', color: '#888' }}>
        &copy; {new Date().getFullYear()} MediTrack AI. All rights reserved.
      </p>
    </div>
  );
}

export default Home;
