// C:\Users\ASUS\meditrack-ai\frontend\src\App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { AuthProvider, useAuth } from './contexts/AuthContext'; // Ensure this path is correct
import Signup from './components/Signup';
import Login from './components/Login';
import Dashboard from './components/Dashboard';
import Home from './components/Home'; // You'll create this Home component

// A PrivateRoute component to protect routes that require authentication
function PrivateRoute({ children }) {
  const { currentUser, loading } = useAuth();

  if (loading) {
    // Optionally render a loading spinner or message while checking auth status
    return <div style={{ textAlign: 'center', marginTop: '50px', fontSize: '1.2em' }}>Checking authentication...</div>;
  }

  // If user is not logged in, redirect to login page
  if (!currentUser) {
    return <Navigate to="/login" replace />;
  }

  // If user is logged in, render the children (the protected component)
  return children;
}

function App() {
  return (
    <Router>
      <AuthProvider> {/* Wrap your entire application with AuthProvider */}
        <div className="app-container" style={{ fontFamily: 'Arial, sans-serif' }}>
          <h1 style={{ textAlign: 'center', color: '#333', padding: '20px', background: '#f0f0f0', borderBottom: '1px solid #ddd' }}>MediTrack AI</h1>
          <Routes>
            <Route path="/" element={<Home />} /> {/* Landing page */}
            <Route path="/signup" element={<Signup />} />
            <Route path="/login" element={<Login />} />
            <Route
              path="/dashboard"
              element={
                <PrivateRoute>
                  <Dashboard />
                </PrivateRoute>
              }
            />
            {/* Catch-all route for any undefined paths, redirects to home */}
            <Route path="*" element={<Navigate to="/" replace />} />
          </Routes>
        </div>
      </AuthProvider>
    </Router>
  );
}

export default App;
