// C:\Users\ASUS\meditrack-ai\frontend\src\components\Dashboard.js
import React from 'react';
import { useAuth } from '../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import ReportUploadForm from './ReportUploadForm';

function Dashboard() {
  const { currentUser, userProfile, logout, loading } = useAuth();
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
    } catch (error) {
      console.error('Error logging out:', error);
      alert('Failed to log out. Please try again.');
    }
  };

  if (loading) {
    return <div style={{ textAlign: 'center', marginTop: '50px', fontSize: '1.2em', color: '#666' }}>Loading dashboard...</div>;
  }

  if (!currentUser) {
    navigate('/login');
    return null;
  }

  return (
    <div style={{
      fontFamily: 'Inter, sans-serif', /* Apply Inter here as well */
      maxWidth: '1200px', /* Wider dashboard */
      margin: '20px auto',
      border: '1px solid #e0e0e0', /* Lighter border */
      borderRadius: '12px', /* More rounded corners */
      boxShadow: '0 5px 15px rgba(0,0,0,0.08)', /* Softer, larger shadow */
      backgroundColor: '#fff'
    }}>
      <nav style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        padding: '20px 30px', /* More padding */
        background: '#f8f8f8', /* Lighter background */
        borderBottom: '1px solid #e0e0e0',
        borderRadius: '12px 12px 0 0'
      }}>
        <h2 style={{ margin: 0, color: '#333', fontSize: '1.8em', fontWeight: 600 }}>
          Welcome, {userProfile ? userProfile.name : currentUser.email}!
        </h2>
        <button
          onClick={handleLogout}
          style={{
            padding: '12px 25px', /* More padding */
            background: '#dc3545', /* Red for logout */
            color: 'white',
            fontSize: '1em'
          }}
        >
          Logout
        </button>
      </nav>

      <div style={{ padding: '30px' }}>
        <h3 style={{ color: '#007bff', marginBottom: '25px', fontSize: '2em', fontWeight: 600 }}>Your Meditrack AI Dashboard</h3>
        <p style={{ fontSize: '1.1em', color: '#555', lineHeight: '1.6', marginBottom: '35px' }}>
          Here you can upload your medical reports for AI analysis and view your past records.
        </p>

        <ReportUploadForm />

        <div style={{ marginTop: '50px', borderTop: '1px solid #eee', paddingTop: '40px' }}>
          <h4 style={{ color: '#333', fontSize: '1.6em', fontWeight: 600 }}>Recent Reports & History</h4>
          <p style={{ color: '#777', fontSize: '1em' }}>No recent reports to display yet. Upload one above to get started!</p>
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
