// C:\Users\ASUS\meditrack-ai\frontend\src\components\Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db, createUserWithEmailAndPassword, doc, setDoc } from '../firebase';

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      await setDoc(doc(db, "users", user.uid), {
        uid: user.uid,
        email: user.email,
        name: name,
        createdAt: new Date(),
      });

      console.log('User signed up successfully and profile created in Firestore:', user);
      navigate('/dashboard');
    } catch (err) {
      if (err.code === 'auth/email-already-in-use') {
        setError('This email is already in use. Please try logging in or use a different email.');
      } else if (err.code === 'auth/invalid-email') {
        setError('The email address is not valid.');
      } else if (err.code === 'auth/weak-password') {
        setError('Password is too weak. Please choose a stronger password (at least 6 characters).');
      } else {
        setError(err.message);
      }
      console.error('Error signing up:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      maxWidth: '450px', /* Wider form */
      margin: '50px auto',
      padding: '40px', /* More padding */
      border: '1px solid #e0e0e0', /* Lighter border */
      borderRadius: '12px', /* More rounded corners */
      boxShadow: '0 5px 15px rgba(0,0,0,0.08)', /* Softer, larger shadow */
      backgroundColor: '#fff'
    }}>
      <h2 style={{ textAlign: 'center', color: '#333', marginBottom: '30px', fontSize: '2em', fontWeight: 700 }}>Create Your Account</h2>
      <form onSubmit={handleSignup} style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
        <input
          type="text"
          placeholder="Full Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          disabled={loading}
        />
        <input
          type="email"
          placeholder="Email Address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
        />
        <input
          type="password"
          placeholder="Password (min 6 characters)"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
        />
        <button type="submit" disabled={loading} style={{
          padding: '12px 15px',
          background: '#6f42c1', /* Purple button */
          color: 'white',
          fontSize: '1.1em'
        }}>
          {loading ? 'Signing Up...' : 'Sign Up'}
        </button>
      </form>
      {error && <p style={{ color: '#dc3545', marginTop: '20px', textAlign: 'center', fontWeight: 500 }}>{error}</p>}
      <p style={{ marginTop: '25px', textAlign: 'center', fontSize: '0.95em' }}>
        Already have an account? {' '}
        <button onClick={() => navigate('/login')} style={{
          background: 'none',
          border: 'none',
          color: '#007bff',
          cursor: 'pointer',
          textDecoration: 'underline',
          padding: '0',
          fontSize: '0.95em',
          fontWeight: 500
        }}>
          Login
        </button>
      </p>
    </div>
  );
}

export default Signup;
