// C:\Users\ASUS\meditrack-ai\frontend\src\components\Signup.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { auth, db, createUserWithEmailAndPassword, doc, setDoc } from '../firebase';

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [name, setName] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleSignup = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    try {
      // 1. Create user with Email and Password using Firebase Auth SDK
      const userCredential = await createUserWithEmailAndPassword(auth, email, password);
      const user = userCredential.user;

      // 2. Store additional user data in Firestore
      await setDoc(doc(db, "users", user.uid), {
        uid: user.uid,
        email: user.email,
        name: name,
        createdAt: new Date(),
      });

      console.log('User signed up successfully and profile created in Firestore:', user);
      navigate('/dashboard'); // Redirect to dashboard after successful signup
    } catch (err) {
      if (err.code === 'auth/email-already-in-use') {
        setError('This email is already in use. Please try logging in or use a different email.');
      } else if (err.code === 'auth/invalid-email') {
        setError('The email address is not valid.');
      } else if (err.code === 'auth/weak-password') {
        setError('Password is too weak. Please choose a stronger password (at least 6 characters).');
      } else {
        setError(err.message);
      }
      console.error('Error signing up:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="signup-container" style={{maxWidth: '400px', margin: '50px auto', padding: '30px', border: '1px solid #ddd', borderRadius: '8px', boxShadow: '0 2px 5px rgba(0,0,0,0.1)'}}>
      <h2 style={{textAlign: 'center', color: '#333'}}>Sign Up</h2>
      <form onSubmit={handleSignup} style={{display: 'flex', flexDirection: 'column', gap: '15px'}}>
        <input
          type="text"
          placeholder="Full Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          required
          disabled={loading}
          style={{padding: '10px', borderRadius: '4px', border: '1px solid #ccc'}}
        />
        <input
          type="email"
          placeholder="Email Address"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
          disabled={loading}
          style={{padding: '10px', borderRadius: '4px', border: '1px solid #ccc'}}
        />
        <input
          type="password"
          placeholder="Password (min 6 characters)"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          disabled={loading}
          style={{padding: '10px', borderRadius: '4px', border: '1px solid #ccc'}}
        />
        <button type="submit" disabled={loading} style={{padding: '10px 15px', background: '#007bff', color: 'white', border: 'none', borderRadius: '4px', cursor: 'pointer'}}>
          {loading ? 'Signing Up...' : 'Sign Up'}
        </button>
      </form>
      {error && <p style={{ color: 'red', marginTop: '15px', textAlign: 'center' }}>{error}</p>}
      <p style={{ marginTop: '20px', textAlign: 'center' }}>
        Already have an account? {' '}
        <button onClick={() => navigate('/login')} className="link-button" style={{background: 'none', border: 'none', color: '#007bff', cursor: 'pointer', textDecoration: 'underline'}}>
          Login
        </button>
      </p>
    </div>
  );
}

export default Signup;
