// C:\Users\ASUS\meditrack-ai\frontend\src\components\ReportUploadForm.js
import React, { useState } from 'react';
import { useAuth } from '../contexts/AuthContext';

function ReportUploadForm() {
  const { currentUser } = useAuth();
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [analysisResult, setAnalysisResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  const handleFileChange = (event) => {
    setAnalysisResult(null);
    setError(null);
    setSelectedFiles(Array.from(event.target.files));
  };

  const handleSubmitReports = async (event) => {
    event.preventDefault();
    setError(null);
    setAnalysisResult(null);

    if (!currentUser) {
      setError('You must be logged in to analyze reports.');
      setLoading(false);
      return;
    }
    if (selectedFiles.length === 0) {
      setError('Please select at least one file to upload.');
      setLoading(false);
      return;
    }

    setLoading(true);

    try {
      const idToken = await currentUser.getIdToken();
      console.log('Firebase ID Token being sent:', idToken ? 'Token exists' : 'Token is missing!');

      const formData = new FormData();
      selectedFiles.forEach(file => {
        formData.append('reportFiles', file);
      });

      const response = await fetch('http://localhost:3001/api/analyze-reports', {
        method: 'POST',
        headers: {
          'Authorization': `Bearer ${idToken}`,
        },
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || errorData.details || 'Failed to analyze reports due to server error.');
      }

      const data = await response.json();
      setAnalysisResult(data);
      console.log('Analysis successful:', data);
      setSelectedFiles([]);

    } catch (err) {
      console.error('Error analyzing reports:', err);
      setError(`Failed to fetch analysis: ${err.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{
      padding: '30px', /* More padding */
      border: '1px solid #e0e0e0',
      borderRadius: '12px', /* More rounded corners */
      boxShadow: '0 3px 10px rgba(0,0,0,0.06)', /* Softer shadow */
      backgroundColor: '#fff'
    }}>
      <h3 style={{ color: '#333', marginBottom: '20px', fontSize: '1.8em', fontWeight: 600 }}>Upload Medical Reports for Analysis</h3>
      <p style={{ color: '#666', marginBottom: '30px', fontSize: '1em' }}>Upload image files (JPG, PNG, etc.) of your medical reports. Max 5MB per file.</p>
      <form onSubmit={handleSubmitReports} style={{ display: 'flex', flexDirection: 'column', gap: '20px' }}>
        {/* Reusing the global .file-input-wrapper from index.css */}
        <label htmlFor="file-upload" className="file-input-wrapper">
          <span>Choose Files</span>
          <input id="file-upload" type="file" accept="image/*" multiple onChange={handleFileChange} disabled={loading} />
        </label>
        {selectedFiles.length > 0 && (
          <div style={{ marginTop: '10px', fontSize: '0.9em', color: '#555' }}>
            <p>Selected files:</p>
            <ul style={{ listStyleType: 'disc', paddingLeft: '20px' }}>
              {selectedFiles.map((file, index) => (
                <li key={index}>{file.name}</li>
              ))}
            </ul>
          </div>
        )}
        <button
          type="submit"
          disabled={loading || selectedFiles.length === 0}
          style={{
            padding: '12px 25px', /* More padding */
            background: '#6f42c1', /* Purple button */
            color: 'white',
            fontSize: '1.1em'
          }}
        >
          {loading ? 'Analyzing...' : 'Analyze Reports'}
        </button>
      </form>

      {error && (
        <p style={{ color: '#dc3545', marginTop: '20px', fontWeight: 500, border: '1px solid #dc3545', padding: '10px', borderRadius: '8px', backgroundColor: '#ffe0e0' }}>
          Error: {error}
        </p>
      )}

      {analysisResult && (
        <div style={{ marginTop: '30px', borderTop: '1px solid #eee', paddingTop: '30px' }}>
          <h4 style={{ color: '#007bff', marginBottom: '20px', fontSize: '1.6em', fontWeight: 600 }}>Analysis Feedback from AI:</h4>
          <div style={{
            whiteSpace: 'pre-wrap',
            background: '#f0faff', /* Light blue background for feedback */
            padding: '20px',
            borderRadius: '12px',
            border: '1px solid #d0e0ff',
            fontSize: '1.05em', /* Slightly larger font */
            lineHeight: '1.7', /* More readable line spacing */
            color: '#222', /* Darker text for readability */
            boxShadow: 'inset 0 1px 3px rgba(0,0,0,0.05)'
          }}>
            {/* You would use ReactMarkdown here if feedback contains Markdown */}
            {analysisResult.aiFeedback}
          </div>

          {analysisResult.extractedTexts && analysisResult.extractedTexts.length > 0 && (
            <div style={{ marginTop: '30px' }}>
              <h5 style={{ color: '#333', marginBottom: '15px', fontSize: '1.4em', fontWeight: 600 }}>Extracted Texts (from individual images):</h5>
              <ul style={{ listStyleType: 'none', padding: 0 }}>
                {analysisResult.extractedTexts.map((item, index) => (
                  <li key={index} style={{ marginBottom: '15px', background: '#e9f7ef', padding: '15px', borderRadius: '10px', border: '1px solid #d4edda', boxShadow: '0 2px 5px rgba(0,0,0,0.03)' }}>
                    <strong style={{ color: '#28a745', fontSize: '1.1em' }}>File: {item.fileName}</strong> (
                      <a href={item.cloudinaryUrl} target="_blank" rel="noopener noreferrer" style={{ color: '#007bff', textDecoration: 'none', marginLeft: '8px', fontSize: '0.9em' }}>
                        View Original Image
                      </a>
                    )
                    <pre style={{ whiteSpace: 'pre-wrap', fontSize: '0.95em', color: '#444', marginTop: '10px', background: '#f0f0f0', padding: '10px', borderRadius: '8px', border: '1px solid #e0e0e0' }}>
                      {item.text}
                    </pre>
                  </li>
                ))}
              </ul>
            </div>
          )}

          {analysisResult.uploadedUrls && analysisResult.uploadedUrls.length > 0 && (
              <div style={{ marginTop: '25px' }}>
                <h5 style={{ color: '#333', marginBottom: '15px', fontSize: '1.4em', fontWeight: 600 }}>All Uploaded Image URLs (Cloudinary):</h5>
                <ul style={{ listStyleType: 'disc', paddingLeft: '25px', fontSize: '0.95em', color: '#555' }}>
                  {analysisResult.uploadedUrls.map((url, index) => (
                    <li key={index} style={{ marginBottom: '8px' }}>
                      <a href={url} target="_blank" rel="noopener noreferrer" style={{ color: '#007bff', wordBreak: 'break-all', textDecoration: 'underline' }}>
                        {url}
                      </a>
                    </li>
                  ))}
                </ul>
              </div>
          )}
        </div>
      )}
    </div>
  );
}

export default ReportUploadForm;
